import React, { Component } from "react";
import { Table } from 'react-bootstrap';
import axios from 'axios'
import CreateAmiibo from "./modals/CreateAmiibo";
import Button from 'react-bootstrap/Button';
/*import SendMessage from "./modals/sendMessage";*/
/*import { Button } from 'react-bootstrap';*/

export default class Amiibos extends Component {
    state = {
        amiibos: [],
        amiibosfav: []
    }
    async componentDidMount() {
        const res = await axios.get(process.env.REACT_APP_HOSTNAME + "/amiibos/user/" + localStorage.getItem("user_id"));
        this.setState({ amiibosfav: res.data });
    }

    eliminarAmiibo = async(id) =>{
        await axios.delete(process.env.REACT_APP_HOSTNAME+"/amiibo/" + id);
        window.location.reload();
    }
    
    render() {
        return (
            <div className="container">
                <h1>Amiibos</h1>
                <div className="row">
                    <div className="col-lg-6">
                    <h3>Lista de Amiibos Favoritos</h3>
                     <Table striped bordered hover>
                         <thead>
                             <tr>
                              <th>Personaje</th>
                              <th>Nombre</th>
                              <th>Tail</th>
                         </tr>
                     </thead>
                    <tbody>
                        {
                            this.state.amiibosfav.map(amiibofav =>
                                <tr key={amiibofav._id}>
                                    <td>{amiibofav.character}</td>
                                    <td>{amiibofav.name}</td>
                                    <td>{amiibofav.tail}</td>
                                    <td><Button variant="secondary" size="sm">Eliminar</Button></td>
                               </tr>
                            )
                        }
                    </tbody>
                 </Table>
                </div>
                <div className="col-lg-6">
                <h3>Lista de Amiibos</h3>
                     <Table striped bordered hover>
                         <thead>
                             <tr>
                              <th>Personaje</th>
                              <th>Nombre</th>
                              <th>Tail</th>
                              <th>actions</th>
                         </tr>
                     </thead>
                    <tbody>
                        {
                            this.state.amiibos.map(amiibo =>
                                <tr key={amiibo._id}>
                                    <td>{amiibo.character}</td>
                                    <td>{amiibo.name}</td>
                                    <td>{amiibo.tail}</td>
                                    <td><Button variant="secondary" size="sm">Agregar</Button></td>
                                </tr>
                            )
                        }
                    </tbody>
                 </Table>
                </div>
              </div>
           </div>
        )
    }
}
